q = @(x) (2*(x^(-2)));
r = @(x) (sin(log(x))*(x^(-2)));
a = 1;
b = 2;
alph = 1;
bet = 2;

[xi, w1i, w2i] = linshoot(p, q, r, a, b, 10, alph, bet);

